import React, { useEffect, useState } from 'react';
import axios from 'axios';
import CustomNavbar from "./Header";
import { Form, Label, Input, FormGroup, Button } from "reactstrap";

function Sales() {
  const [salesdata, setsalesdata] = useState([]);
  const [csvFile, setCsvFile] = useState(null);  // To store uploaded file
  const [question, setQuestion] = useState('');  // To store user input question
  const [output, setOutput] = useState('');      // To store output from API

  useEffect(() => {
    axios
      .get('http://127.0.0.1:8000/project/sales/')
      .then((res) => setsalesdata(res.data));
  }, []);

  // Handle CSV file upload
  const handleFileUpload = async (e) => {
    e.preventDefault();
    
    if (!csvFile) {
      alert('Please upload a CSV file!');
      return;
    }
    
    const formData = new FormData();
    formData.append('csv_file', csvFile);

    try {
      await axios.post('http://127.0.0.1:8000/project/upload-sales-csv/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      alert('CSV uploaded successfully!');
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  // Handle fetching the answer for the question
  const handleGetAnswer = async (e) => {
    e.preventDefault();

    if (!question) {
      alert('Please enter a question!');
      return;
    }

    try {
      const response = await axios.post('http://127.0.0.1:8000/project/generate-single-query/', { question });
      setOutput(response.data.answer || 'No answer found');
    } catch (error) {
      console.error('Error fetching answer:', error);
      setOutput('Error retrieving answer.');
    }
  };

  return (
    <>
      <CustomNavbar />
      <Form style={{ width: "800px", marginTop: "20px", marginLeft: "450px" }}>

        <br />
        {/* CSV File Upload Section */}
        <FormGroup>
          <Label for="csv_file">Upload Your Sales CSV</Label>
          <Input 
            id="csv_file" 
            name="csv_file" 
            type="file" 
            onChange={(e) => setCsvFile(e.target.files[0])}  // Set the uploaded file
          />
        </FormGroup>
        <Button className="btn btn-success" type="submit" onClick={handleFileUpload}>Submit</Button>
        <br /> <br />

        {/* Question Input Section */}
        <h1>Get Sales Info</h1> 
        <br /> 
        <FormGroup>
          <Label for="text">Ask a question</Label>
          <Input 
            id="text" 
            type="textarea" 
            value={question} 
            onChange={(e) => setQuestion(e.target.value)}  // Set the user's question
          />
        </FormGroup>
        <Button className="btn btn-primary" type="submit" onClick={handleGetAnswer}>Get Answer</Button>
        <br /> 

        {/* Output Display Section */}
        <h1>Output</h1> 
        <br />
        <Input 
          id="output" 
          type="textarea" 
          value={output} 
          readOnly  // Make the output textarea read-only
        /> 

      </Form>
    </>
  );
}

export default Sales;
